import {useEffect, useState} from 'react';
import axios from "axios";
import {useNavigate} from 'react-router-dom';
import './App.css';

const baseUrl = "http://localhost:5000"

export function EditQuestion(){

	const [name, setName] = useState('');

	const [questionType, setQuestionType] = useState('Multiple Choice');

	const [correctAnswer, setCorrectAnswer] = useState('');

	const [incorrectAnswer, setIncorrectAnswer] = useState('');

	const [incorrectAnswer2, setIncorrectAnswer2] = useState('');

	const [incorrectAnswer3, setIncorrectAnswer3] = useState('');

	const [truthValue, setTruthValue] = useState('True');

	const navigate = useNavigate();

	useEffect(() => {
		const fetchData = async () => {
			try {
				const response = await axios.get(baseUrl + '/editQuestion');
				setName(response['name']);
				setQuestionType(response['questionType']);
				setIncorrectAnswer(response['incorrectAnswer']);
				setIncorrectAnswer2(response['incorrectAnswer2']);
				setIncorrectAnswer3(response['incorrectAnswer3']);
				if(response['questionType'] === 'True or False'){
					setTruthValue(response['correctAnswer']);
				}
				else{
					setCorrectAnswer(response['correctAnswer']);
				}
			} catch (error) {
				console.error('Error:', error);
			}
		};
		fetchData();
	}, []);

	const handleName = (event) => {
		setName(event.target.value);
	};

	const handleChange = (event) => {
		setQuestionType(event.target.value);
	};

	const handleCorrectAnswer = (event) => {
		setCorrectAnswer(event.target.value);
	};

	const handleIncorrectAnswer = (event) => {
		setIncorrectAnswer(event.target.value);
	};

	const handleIncorrectAnswer2 = (event) => {
		setIncorrectAnswer2(event.target.value);
	};

	const handleIncorrectAnswer3 = (event) => {
		setIncorrectAnswer3(event.target.value);
	};

	const handleTruthValue = (event) => {
		setTruthValue(event.target.value);
	};

	const handleSubmit = async (event) => {
		event.preventDefault();
        try {
			if(questionType === 'Multiple Choice'){
				formData = { name: name, questionType: questionType, correctAnswer: correctAnswer, incorrectAnswer: incorrectAnswer, incorrectAnswer2: incorrectAnswer2, incorrectAnswer3: incorrectAnswer3 };
			}
			else if(questionType === 'True or False'){
				formData = { name: name, questionType: questionType, truthValue: truthValue }
			}
			else{
				formData = { name: name, questionType: questionType, correctAnswer: correctAnswer }
			}
        	const response = await axios.post(baseUrl+'/editQuestion', formData);
			if(response.data['status'] === 'error'){
				alert(response.data['message']);
			}
			else{
				navigate('/editQuiz')
			}
        }
		catch (error) {
        	console.error('Error:', error);
		}
    };

	const handleDelete = async () => {
		const response = await axios.delete(baseUrl+'/editQuestion');
		if(response.data['status'] === 'error'){
			alert(response.data['message']);
		}
		else{
			navigate('/editQuiz')
		}
	};

	return (
    <div className="App">
		<body>
		<h1>Quiz!</h1>
		<div class = "main">
			<h2>Add a Question</h2>
			<form onSubmit={handleSubmit}>
				<h2>Create a Quiz</h2>
				<div className="main">
					<label htmlFor="name">Question: </label>
						<input 
							type="text"
							name="name"
							id="name"
							onChange={handleName}
							value = {name}
						/>
					<label>
						Pick a Question Type:
						<select name="selectedType" onChange={handleChange} value={questionType}>
							<option value="Multiple Choice">Multiple Choice</option>
							<option value="True or False">True or False</option>
							<option value="Short Answer">Short Answer</option>
						</select>
					</label>
				    {(
						<>
						questionType === "Multiple Choice" ?
						<label htmlFor="correctAnswer">Correct Answer: </label>
						<input 
							type="text"
							name="correctAnswer"
							id="correctAnswer"
							onChange={handleCorrectAnswer}
							value = {correctAnswer}
						/>
						<label htmlFor="incorrectAnswer">Incorrect Answer: </label>
						<input 
							type="text"
							name="incorrectAnswer"
							id="incorrectAnswer"
							onChange={handleIncorrectAnswer}
							value = {incorrectAnswer}
						/>
						<label htmlFor="incorrectAnswer2">Incorrect Answer: </label>
						<input 
							type="text"
							name="incorrectAnswer2"
							id="incorrectAnswer2"
							onChange={handleIncorrectAnswer2}
							value = {incorrectAnswer2}
						/>
						<label htmlFor="incorrectAnswer3">Incorrect Answer: </label>
						<input 
							type="text"
							name="incorrectAnswer3"
							id="incorrectAnswer3"
							onChange={handleIncorrectAnswer3}
							value = {incorrectAnswer3}
						/>
						</>
					)}
					{(
						<>
						questionType === "True or False" ?
						<label>
							True or False:
							<select name="trueOrFalse" onChange={handleTruthValue} value = {truthValue}>
								<option value="True">True</option>
								<option value="False">False</option>
							</select>
						</label>
						</>
					)}
					{(
						<>
						questionType === "Short Answer" ?
						<label htmlFor="correctAnswer">Correct Answer: </label>
						<input 
							type="text"
							name="correctAnswer"
							id="correctAnswer"
							onChange={handleCorrectAnswer}
							value = {correctAnswer}
						/>
						</>
					)}
					<button type="button" onClick={handleDelete} className="submit">
						Delete Question
					</button>
				</div>
			</form>
		</div>
		</body>
    </div>
	);
}
